      App.saveState();
      // –ù–µ –∞–≤—Ç–æ-–∞–¥–≤–∞–Ω—Å–∏–º –≤ endless-—Å–ª–æ–≤–∞—Ä—è—Ö
      if (!isEndlessDict(key)) {
        try{ if(App.Sets && App.Sets.checkCompletionAndAdvance) App.Sets.checkCompletionAndAdvance(); }catch(e){}
      }
